deviceProfile.payloadDecoderScript:
function Decode(fPort, bytes, variables) {
    var payload = {};

    // - [0-1] (2) - Last 2 bytes of the DevEui
    // - [2]   (1) - Type of payload (0 for sensor readings)
    // - [3-6] (4) - Data
    //              - [3-4] - Pressure
    //              - [5-6] - Temperature
    // - [7]   (1) - Battery voltage in tenths of a volt
    if (bytes[2] != 0) {
        return {};
    }

    // Note that if the pressure field is greater than 0x8000, the
    //  device reads a negative pressure which is equal to twoâ€™s complement
    //  of the pressure field.
    payload.pressure = ((bytes[3] << 8) | bytes[4]);
    if (payload.pressure > 0x8000) {
        // TODO: test which one for negative pressure
        // payload.pressure = (payload.pressure & 0x7FFF) * -1;
        payload.pressure *= -1;
    }

    payload.temperature = (bytes[5] << 8) | bytes[6];
    if (payload.temperature > 0x8000) {
        // TODO: test which one for negative temperature
        // payload.temperature = (payload.temperature & 0x7FFF) * -1;
        payload.temperature *= -1;
    }
    payload.temperature *= 0.01;

    payload.voltage = (bytes[bytes.length - 1] * 0.1);

    return payload;
}
