deviceProfile.payloadDecoderScript:
function Decode(fPort, bytes, variables) {
  var payload = {};
  for(var i = 0; i < bytes.length; ){    
    //TODO: offset usage
    var offset = bytes[i] >> 6;
    var type = bytes[i] & 63;
    i++;
    switch(type){
      case 1: //temperature
        payload.temperature = ((((bytes[i] << 8) & 0x7fff) | (bytes[i+1])) * (((bytes[i] >> 7) & 0x01) ? -1 : 1)) / 10;;
		i += 2;
        break;
      case 2: //humidity
        payload.humidity = bytes[i];
        i += 1;
        break;
      case 4: //light
        payload.light = (bytes[i] << 8) + (bytes[i+1]);
        i += 2;
        break;
      case 5: //motion
        payload.motion = bytes[i];
        i += 1;
        break;
      case 6: //CO2
        payload.co2 = (bytes[i] << 8) + (bytes[i+1]);
        i += 2;
        break;
      case 7: //voltage
        payload.voltage = ((bytes[i] << 8) + (bytes[i+1])) / 1000;
        i += 2;
        break;
      case 0x3D: //debug info
        i += 4;
       	break;
      case 0x3E: //sensor settings //TODO:
        i = bytes.length;
        break;
    }
  }
  return payload;
}
